cy.intercept("GET", "**/api/dashboard/stats", req => {
  req.reply({
    delay: 1000,
    statusCode: 200,
    body: {
      activeProjects: 10,
      totalProjects: 20,
      totalUsers: 50,
      totalFiles: 100,
      completedSprints: 15
    }
  });
}).as("delayedStats");
cy.visit("/dashboard");
cy.get('[data-cy="dashboard-loading"]').should("be.visible");
cy.wait("@delayedStats");
cy.get('[data-cy="dashboard-loading"]').should("not.exist");

cy.intercept("GET", "**/api/dashboard/stats", {
  statusCode: 500,
  body: {
    error: "Internal Server Error"
  }
}).as("getStatsError");
cy.visit("/dashboard");
cy.get('[data-cy="dashboard-error"]').should("be.visible").and("contain", "Internal Server Error");

cy.get('[data-cy="stats-grid"]').within(() => {
  // Check all stat cards are present
  cy.get('[data-cy^="stat-card-"]').should("have.length", 4);
});

// Check Total Users card
cy.get('[data-cy="stat-card-total-users"]').within(() => {
  cy.get('[data-cy="stat-value-total-users"]').should("contain", "50");
  cy.get('[data-cy="stat-description-total-users"]').should("contain", "Team members");
});
// Check Test Files card
cy.get('[data-cy="stat-card-test-files"]').within(() => {
  cy.get('[data-cy="stat-value-test-files"]').should("contain", "100");
  cy.get('[data-cy="stat-description-test-files"]').should("contain", "Total test files");
});
// Check Completed Sprints card
cy.get('[data-cy="stat-card-completed-sprints"]').within(() => {
  cy.get('[data-cy="stat-value-completed-sprints"]').should("contain", "15");
  cy.get('[data-cy="stat-description-completed-sprints"]').should("contain", "Successfully completed");
});

// Test mobile view
cy.viewport("iphone-6");
cy.get('[data-cy="stats-grid"]').should("have.class", "grid-cols-1");
// Test desktop view
cy.viewport(1024, 768);
cy.get('[data-cy="stats-grid"]').should("have.class", "md:grid-cols-4");

cy.intercept("GET", "**/api/user/profile", {
  statusCode: 200,
  body: {
    name: "John Doe"
  }
}).as("getUserProfile");
cy.visit("/dashboard");
cy.get('[data-cy="dashboard-welcome"]').should("contain", "Welcome back, User");
